import React from 'react';
import './App.css';
import Pokemons from './data';

class Card extends React.Component {
  render() {
    const { data, filter } = this.props;
    return (
      data.filter(({ type }) => ['Eletric', 'Fire', 'Bug', 'Poison', 'Psychic', 'Normal', 'Dragon', 'All'].includes(filter === type))
        .map(({ id, name, type, averageWeight, image }) => {
          <li key={id}>
            <div>
              <img src={image} />
            </div>
            <div>
              {name} type: {type} averageWeight: {averageWeight.value} {averageWeight.measurementUnit}
            </div>
          </li>
        })

    )
  }
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({ type: 'All' })
  }

  serFilterType = event => {
    this.state({
      type: event.target.value === '' ? 'All' : event.target.value
    })
  }
  render() {
    return (
      <div className="App">
        <div className="pokedex">
        </div>
      </div>
    );
  }
}

export default App;
