import React from 'react';
import './App.css';
import Pokemons from './data';

class Card extends React.Component {
  render() {
    const { data, filter } = this.props;
    return (
      data.filter(({ type }) => [type, 'All'].includes(filter))
        .map(({ id, name, type, averageWeight, image }) => (
          <li key={name} className="hidden-card">
            <div>
              <img src={image} alt={name} />
            </div>
            <div>
              {id} - {name} type: {type} averageWeight: {averageWeight.value} {averageWeight.measurementUnit}
            </div>
          </li>
        ))

    )

  }
}

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = ({ type: 'All', index: 0 })
  }

  setNext = async () => {
    const li = document.getElementsByTagName('li');

const i = this.state.index
const state = async (x) => await this.setState(x)
(li.length -1 > i) ? state({index: i + 1}) : state({index: 0});

for(let z = 0; z < li.length; z++ ){li[z].classList.remove('card')}
await li[i].classList.add('card')

  }

  setPreviously = event => {

  }

  setFilterType = event => {

    this.setState({
      type: event.target.value === '' ? 'All' : event.target.value
    })
  }
  render() {
    return (
      <div className="App">
        <div className="pokedex">
          <select className="element-type" value={this.state.type} onChange={this.setFilterType}>
            <option value="Eletric" >Eletric</option>
            <option value="Fire">Fire</option>
            <option value="Bug">Bug</option>
            <option value="Poison">Poison</option>
            <option value="Psychic">Psychic</option>
            <option value="Normal">Normal</option>
            <option value="Dragon">Dragon</option>
            <option value="All">All</option>
          </select>
          <div className="pokedex-screen">
              <Card  filter={this.state.type} data={Pokemons} />
          </div>
          <button onClick={this.setNext} id="next-pokemon" />
          <button onClick={this.setNext} id="previously-pokemon" />
        </div>
      </div>
    );
  }
}
